mapper.update:
UPDATE member
SET member_pw = #{memberPw}, pwcktime = NOW()
WHERE member_Id = #{memberId} 
AND email = #{email}

mapper.select:
SELECT 
	member_id memberId
  , member_role memberRole
FROM member
WHERE member_id = #{memberId}
AND member_pw = #{memberPw}

mapper.update:
UPDATE member
member_pw = #{memberPw}, member_role = #{memberRole},
WHERE member_id = #{memberId}
